    private void bnameKeyPressed(java.awt.event.KeyEvent evt) {//GEN-FIRST:event_bnameKeyPressed
        //buyer name box enter key
        if(evt.getKeyCode()==KeyEvent.VK_ENTER){
            if(bname.getText().isEmpty()){
       JOptionPane.showMessageDialog(this,"Buyer is Empty !!","Please Enter Name",JOptionPane.WARNING_MESSAGE);
        }else{
        bcn.requestFocus();}
        }
       
         else if(evt.getKeyCode()==KeyEvent.VK_DOWN){
            if(bname.getText().isEmpty()){
       JOptionPane.showMessageDialog(this,"Buyer is Empty !!","Please Enter Name",JOptionPane.WARNING_MESSAGE);
        }else{
        bcn.requestFocus();}
        }
    }//GEN-LAST:event_bnameKeyPressed

