    private void bgenKeyPressed(java.awt.event.KeyEvent evt) {//GEN-FIRST:event_bgenKeyPressed
        // gender combobox KeyPress
        if(evt.getKeyCode()==KeyEvent.VK_ENTER){
        if(bgen.getSelectedIndex()==0){
            JOptionPane.showMessageDialog(this,"NOT Seleted Gender","Give this Info",JOptionPane.WARNING_MESSAGE);
        }else{
        bgen.requestFocus();}
        }
    }//GEN-LAST:event_bgenKeyPressed
 

    private void jButton1ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton1ActionPerformed
        //Save
        String bn=bname.getText();
        String bc=bcn.getText();
        String be=bei.getText();
        String ba=badd.getText();
        String bg=bgen.getSelectedItem().toString();
        try{
        Connection con=connection.getcon();
        Statement st=con.createStatement();
        int save=st.executeUpdate("insert into newbuyer values('"+bn+"','"+bc+"','"+be+"','"+ba+"','"+bg+"')");
        if(save>0){
        JOptionPane.showMessageDialog(this,"Data Save Sucesessfully!!!");
            setVisible(false);
            new newBuyer().setVisible(true);
        }
        
        else{
             JOptionPane.showMessageDialog(this,"Data Save Unsucsessful!!!");
        }
        
        }
        catch(Exception e){
        JOptionPane.showMessageDialog(this,"Mobile Number Already Done!!!!");}
        
    }//GEN-LAST:event_jButton1ActionPerformed

